insert into culture(id, name, description, period_Description, culture_Map, start_Year, end_Year) values (1,'Ancienne Egypte', 'L''Égypte antique est une ancienne civilisation du nord-est de l''Afrique, concentrée le long du cours inférieur du Nil, dans ce qui constitue aujourd''hui l''Égypte.', 'Du 4e millénaire au 1er siècle avant notre ère', 'https://upload.wikimedia.org/wikipedia/commons/thumb/d/d9/Ancient_Egypt_map-fr.svg/200px-Ancient_Egypt_map-fr.svg.png', -3150, -30);
insert into culture(id, name, description, period_Description, culture_Map, start_Year, end_Year) values (2,'Rome Antique', 'La Rome antique est à la fois la ville de Rome et l''État qu''elle fonde dans l''Antiquité. L''idée de Rome antique est inséparable de celle de la culture latine. Ce regroupement de villages au viiie siècle av. J.-C. parvint à dominer l''ensemble du monde méditerranéen et de l''Europe de l''Ouest du ier au ve siècle par la conquête militaire et par l''assimilation des élites locales. Sa domination a laissé d''importantes traces archéologiques et de nombreux témoignages littéraires. Elle façonne encore aujourd''hui l''image de la civilisation occidentale. Durant ces siècles, la civilisation romaine passe d''une monarchie à une république oligarchique puis à un empire autocratique.', 'VIIIe siècle ACN au Ve siècle PCN', 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/6c/Carte_de_l%27Empire_romain_en_118_apr%C3%A8s_J.-C.jpg/220px-Carte_de_l%27Empire_romain_en_118_apr%C3%A8s_J.-C.jpg', -753, 476 );
insert into culture(id, name, description, period_Description, culture_Map, start_Year, end_Year) values (3,'Celte', 'Les Celtes ont constitué lors de la protohistoire européenne un groupe de populations1 indo-européennes parlant des langues celtiques et présentant une certaine unité culturelle2, bien que les interactions culturelles de l''Europe celtique ancienne demeurent incertaines et controversées3. Par ailleurs, la connaissance du processus de diffusion territoriale des anciennes populations celtiques reste à améliorer, en particulier quant à celles des Îles Britanniques et de la péninsule Ibérique', 'XIIe siècle ACN au Ier siècle ACN', 'https://upload.wikimedia.org/wikipedia/commons/thumb/e/ee/Hallstatt_LaTene.png/220px-Hallstatt_LaTene.png', -1350, -6);
insert into culture(id, name, description, period_Description, culture_Map, start_Year, end_Year) values (4,'Aztèqye', 'Les Aztèques, ou Mexicas (du nom de leur capitale, Mexico-Tenochtitlan), étaient un peuple amérindien du groupe nahua, c''est-à-dire de langue nahuatl.', 'XIIIe au XVIe siècle de notre ère', 'https://upload.wikimedia.org/wikipedia/commons/thumb/b/b1/Aztec_Empire_1519_map-fr.svg/langfr-280px-Aztec_Empire_1519_map-fr.svg.png', 1200, 1521);
